import { useEffect, useState } from "react";

export const useFetchMoviesId = (movieId: string | undefined) => {
  const [movie, setMovie] = useState<IMovies>();

  useEffect(() => {
    fetch(`https://my-json-server.typicode.com/cristianmeelo/cinetag-api/movies?id=${movieId}`)
      .then((resp) => resp.json())
      .then((data) => setMovie({ ...data }));
  }, [movieId]);

  return { movie };
};
