import styles from "./index.module.css";
import cover from "./images/banner-home.png";
import { useEffect, useState } from "react";

export const Banner = ({ image }: BannerProps) => {
  //   const coverImage: string = require(`./images/banner-${image}.png`).default;
  const [coverImage, setcoverImage] = useState(
    require(`./images/banner-${image}.png`).default
  );

  useEffect(() => {
    setcoverImage(require(`./images/banner-${image}.png`).default);
  }, [coverImage, image]);

  return (
    <div className={styles.cover}>
      <img src={cover} alt="cover" />
    </div>
  );
};
