import styles from "./index.module.css";
import favoriteIcon from "./favorite.png";
import disfavorIcon from "./disfavor.png";
import { useFavoriteContext } from "context/favoriteContext";

export const Card = ({ id, title, cover }: CardProps) => {
  const { favorite, addFavorite } = useFavoriteContext();
  const isFavorite = favorite.some((fav) => fav.id === id);
  const icon = isFavorite ? favoriteIcon : disfavorIcon;

  return (
    <div className={styles.container}>
      <img src={cover} alt={title} className={cover} />
      <h2>{title}</h2>
      <img src={icon} alt="favorite movie" className={styles.favorite} onClick={() => addFavorite({ id, title, cover })} />
    </div>
  );
};
