import { useEffect, useState } from "react";

export const useFetchMoviesId = (movieId: string | undefined) => {
  const [movie, setMovie] = useState<IMovies>();

  useEffect(() => {
    fetch(`https://my-json-server.typicode.com/cristianmeelo/cinetag-api/movies?=${movieId}`)
      .then((response) => response.json())
      .then((data) => {
        if (Array.isArray(data)) {
          setMovie(...data);
        }
      });
  }, [movieId]);

  return { movie };
};
